import {useDispatch, useSelector} from "react-redux";
import { Link } from "react-router-dom";
import { getPosts, selectAllPosts } from "../postsSlice";
import {useEffect} from "react";

export const PostsList =()=>{

    const dispatch= useDispatch()

    //state.posts kom van postsSlice lijn10
    const news = useSelector(selectAllPosts);

    const newsStatus = useSelector (state=>state.news.status)

    useEffect(()=>{
        if(newsStatus ==='idle'){
            dispatch(getPosts())

        };
    }, [newsStatus, dispatch])

//Hier zou je array kunnen sorteren

    const renderedPosts= news.map(post=>(
        <article key={post.id} className='post-excerpt'>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
            <Link to={`/posts/${post.id}`} className="button muted-button">View post</Link>
        </article>
    ));

    return(
        <section className='posts-list'>
            <h2>Lijst met Artikelen</h2>
            {renderedPosts}
        
        </section>
    )
        
    
}
