import {createAsyncThunk, createSlice, nanoid,client} from "@reduxjs/toolkit"


const initialState = {
  posts:[
    {id:'1', title:'Title1', content:'dit is wat tekst 1',date:'2016-01-04 10:34:23', user:'1'},
    {id:'2', title:'Title2', content:'dit is wat tekst 2',date:'2016-01-04 10:34:23',user:'2'}
    ],
    status:'idle',
    error: null
}

//api geven
export const getPosts=createAsyncThunk(
    'posts/getPosts',
    async()=>{
        const response= await client.get('fakeApi/posts')
    }
);

//posts object


const postsSlice = createSlice({
    name:'posts',
    initialState,
    reducers:{

        postAdded: {
            reducer(state, action){
            state.posts.push(action.payload)
            },
            prepare(title, content,userId){
                return{
                    payload:{
                        id:nanoid(),
                        title,
                        content,
                        date:new Date().toISOString(),
                        user:userId
                    }
                }
            }
        },

        // postAdded(state, action){
        //     // state:huidige state
        //     state.push(action.payload)
        // },

        postUpdated(state, action){
            // in action.payload.id, .title, .content
            const {id, title, content}= action.payload
            const existingPost = state.posts.find(post => post.id===id)

            if(existingPost){
                existingPost.title=title;
                existingPost.content=content;
            }

        }
    }
})

export const {postAdded,postUpdated} = postsSlice.actions
export default postsSlice.reducer

export const selectAllPosts= state =>state.posts.posts
export const selectPostById = (state, postId) =>state.posts.posts.find(post => post.id === postId)
