import { configureStore } from '@reduxjs/toolkit'
import postsReducer from '../features/postsSlice'
import usersReducer from '../features/posts/users/userSlice'

export default configureStore({
  reducer: {
    posts: postsReducer,
    users: usersReducer
    //users: usersRre voorbeeld

  }
})
