import {useDispatch, useSelector} from "react-redux";
import { Link } from "react-router-dom";
import {useEffect} from "react";
import { getNews, selectAllNews } from "../features/postsSlice";
  
  export const RepubliekList =()=>{

    const dispatch= useDispatch()
    // dispath(postAdded(title, content, userId))
    //state.posts kom van postsSlice lijn10
    const news = useSelector(selectAllNews);

    const newsStatus = useSelector (state=>state.news.status)

    useEffect(()=>{
        if(newsStatus ==='idle'){
             dispatch(getNews())
        };
    }, [newsStatus, dispatch])
  
    const renderedNews= news.map(news=>(
        <article key={news.id} className='post-excerpt'>
           <div class="card mb-3">
                <div class="row g-0">
                    <div class="col-md-4">
                    {/* <img src="..." class="img-fluid rounded-start" alt="..."> */}
                    </div>
                    <div class="col-md-8">
                    <div class="card-body">
                        
                        <h5 class="card-title">Card title</h5>
                        <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                        <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                    </div>
                    </div>
                </div>
            </div>
            <Link to={`/posts/${news.id}`} className="button muted-button">View post</Link>
        </article>
    ));




    return(
        <section className='posts-list'>
            <h2>Lijst met Artikelen</h2>
            {renderedNews}
        
        </section>
    )

}
