import {createAsyncThunk, createSlice, nanoid} from "@reduxjs/toolkit"


const initialState = {
  newsData:[
    {id:'1', title:'Title1', content:'dit is wat tekst 1',date:'2016-01-04 10:34:23', user:'1'},
    {id:'2', title:'Title2', content:'dit is wat tekst 2',date:'2016-01-04 10:34:23',user:'2'}
    ],
// newsData:[],
    status:'idle',
    error: null
}

//api geven
export const getNews=createAsyncThunk(
    'news/getNews',
    async()=>{      
        try {
            const response =  await fetch('https://www.vrt.be/vrtnws/nl/regio/_jcr_content/par/grid.app.json'); 
            const data = await response.json();
            console.log('url data: ',data)
            inti
              return data
            
           
        } catch (err) {
            // Handle Error Here
            console.error(err);
        }
    }
);

//posts object


const postsSlice = createSlice({
    name:'news',
    initialState,
    reducers:{


        postAdded: {
            reducer(state, action){
            state.newsData.push(action.payload)
            },
            prepare(title, content,userId){
                return{
                    payload:{
                        id:nanoid(),
                        title,
                        content,
                        date:new Date().toISOString(),
                        user:userId
                    }
                }
            }
        },

        newsAdded(state, action){
            getNews()
            console.log('payload= ',action.payload)
            // state:huidige state
            // state.push(action.payload)
        },
        // postAdded(state, action){
        //     // state:huidige state
        //     state.push(action.payload)
        // },

        postUpdated(state, action){
            // in action.payload.id, .title, .content
            const {id, title, content}= action.payload
            const existingPost = state.news.find(post => post.id===id)

            if(existingPost){
                existingPost.title=title;
                existingPost.content=content;
            }

        }
    }
})

export const {postAdded,postUpdated,newsAdded} = postsSlice.actions
export default postsSlice.reducer

export const selectAllNews= state =>state.news.newsData
export const selectPostById = (state, postId) =>state.news.newsData.find(post => post.id === postId)
