));

  return (
    <section>
      <h2>Add a New Post</h2>
      <form>
        <label htmlFor="postTitle">Post Title:</label>
        <input
          type="text"
          id="postTitle"
          name="postTitle"
          value={title}
          onChange={onTitleChanged}
        />
        <label htmlFor="postContent">Content:</label>
        <textarea
          id="postContent"
          name="postContent"
          value={content}
          onChange={onContentChanged}
        />
        <select value={userId} onChange={onSavePostCliked}>
          <option value={""}></option>
          {userOptions}
        </select>
        <br/>
        <button type="button" onClick={onSavePostCliked}>Save Post</button>
        <br/>
      </form>
    </section>
  )
}
