import { useState, useRef,useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { newsPost } from '../postsSlice'

export const AddPostForm = () => {
  const [imageUrls, setImageUrls] = useState('imagess.jpg')
  const [displayId, setDisplayId] = useState('')
  const [title, setTitle] = useState('')
  const [discription, setDiscription] = useState('')
  const [subtitle, setSubtitle] = useState('')  
  const [content, setContent] = useState('')

  const inputRef = useRef()

  // useCustomFetchHook(file)

  const dispath = useDispatch();

  //users ophalen uit de Redux-store
  const provinces = useSelector (state =>state.provinces)

  const fileChangedHandler = e => setImageUrls(e.target.value)
  const onDisplayIdChanged = e => setDisplayId(e.target.value)
  const onTitleChanged = e => setTitle(e.target.value)
  const onDiscriptionChanged = e => setDiscription(e.target.value)
  const onSubtitleChanged = e => setSubtitle(e.target.value)
  const onContentChanged = e => setContent(e.target.value)

  const onSavePostCliked=() =>{
    //je kunt script testen controleerd
    if( title && content){
       //nieuws object gestuurd
      dispath(newsPost(imageUrls, displayId, title,discription,subtitle,content))
      setImageUrls('');
      setDisplayId('');
      setTitle('');
      setDiscription('');
      setSubtitle('');
      setContent('');
    }
   
  }

  useEffect(()=>{
   setImageUrls('imagess.jpg')
}, [])



const provinceOptions =provinces.map(province=>(
  <option key={province.id} value={province.id}>{province.name}</option>
));
//title content userId
const canSave=Boolean(title) && Boolean(content) && Boolean(displayId)
console.log(imageUrls)
  return (
    <section>
      <h2>Add a News</h2>
      <form>
      <label className="label" htmlFor="gallery-image">
            Afbeelding selecteren
          </label>
          
          <input
            type="file"
            // onChange={fileChangedHandler}
            onChange={() => setImageUrls(inputRef.current.files[0])}
                ref={inputRef}
            id="gallery-image"
            accept=".jpg, .jpeg, .png"
          />






    <img src={`assets/images/${imageUrls.name}`} className="img-fluid rounded-start" width="400" height="200" alt="news_image"/>
    
   



        {/* <label htmlFor="imageUrls">Picture :</label>
        <input
          type="text"
          id="imageUrls"
          name="imageUrls"
          value={imageUrls}
          onChange={onPictureChanged}
        /> */}



<br/>
        <label htmlFor="newsDisplay">Display:</label>
        <select id="display" value={displayId} onChange={onDisplayIdChanged}>
          <option value={""}></option>
          {provinceOptions}
        </select>

        <label htmlFor="newsTitle">Title :</label>
        <input
          type="text"
          id="newsTitle"
          name="newsTitle"
          value={title}
          onChange={onTitleChanged}
        />

        <label htmlFor="newsDiscription">Discription :</label>
        <textarea
          id="newsDiscription"
          name="newsDiscription"
          value={discription}
          onChange={onDiscriptionChanged}
        />

         <label htmlFor="newsSubtitle">Subtitel :</label>
        <input
          type="text"
          id="newsSubtitle"
          name="newsSubtitle"
          value={subtitle}
          onChange={onSubtitleChanged}
        />

        <label htmlFor="newsContent">Content :</label>
        <textarea
          id="newsContent"
          name="newsContent"
          value={content}
          onChange={onContentChanged}
        />
        <br/>

        <button type="button" onClick={onSavePostCliked}>Save news</button>
        <br/>
      </form>
    </section>
  )

  // return (
  //   <section>
  //     <h2>Add a New Post</h2>
  //     <form>
  //       <label htmlFor="postTitle">Post Title:</label>
  //       <input
  //         type="text"
  //         id="postTitle"
  //         name="postTitle"
  //         value={title}
  //         onChange={onTitleChanged}
  //       />
  //       <label htmlFor="postContent">Content:</label>
  //       <textarea
  //         id="postContent"
  //         name="postContent"
  //         value={content}
  //         onChange={onContentChanged}
  //       />
  //       <label htmlFor="postAutheur">Auteur:</label>
  //       <select id="Author" value={userId} onChange={onAuthorChanged}>
  //         <option value={""}></option>
  //         {userOptions}
  //       </select>
  //       <br/>

  //       <button type="button" disabled={!canSave} onClick={onSavePostCliked}>Save Post</button>
  //       <br/>
  //     </form>
  //   </section>
  // )
}
