import {useDispatch, useSelector} from "react-redux";
import { Link } from "react-router-dom";
import {useEffect} from "react";
import { getNews, selectAllNews } from "../features/postsSlice";
import Moment from 'moment';
  
  export const RepubliekList =()=>{

    const dispatch= useDispatch()
    // dispath(postAdded(title, content, userId))
    //state.posts kom van postsSlice lijn10
    const news = useSelector(selectAllNews);

    const newsStatus = useSelector (state=>state.news.status)

    useEffect(()=>{
        if(newsStatus ==='idle'){
             dispatch(getNews())
        };
    }, [newsStatus, dispatch])
  

 

    const renderedNews= news.map(news=>(

        
        <article key={news.id} className='post-excerpt'>
           <div class="card mb-3">
                <div class="row g-0">
                    <div class="col-md-4">
                    <img src={news.imageUrls} class="img-fluid rounded-start" alt="..."/>
                    </div>
                    <div class="col-md-8">
                    <div class="card-body">
                        <strong>{news.displayTag}</strong>
                        <h5 class="card-title">{news.title}</h5>
                        <p class="card-text">{news.subtitle}</p>
                        <p class="card-text"><small class="text-muted">update <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-clock" viewBox="0 0 16 16">
  <path d="M8 3.5a.5.5 0 0 0-1 0V9a.5.5 0 0 0 .252.434l3.5 2a.5.5 0 0 0 .496-.868L8 8.71V3.5z"/>
  <path d="M8 16A8 8 0 1 0 8 0a8 8 0 0 0 0 16zm7-8A7 7 0 1 1 1 8a7 7 0 0 1 14 0z"/>
</svg>{Moment(news.articleDates.updateDate).format('H:mm') }</small></p>
                    </div>
                    </div>
                </div>
            </div>
            <Link to={`/posts/${news.id}`} className="button muted-button">View post</Link>
        </article>
    ));




    return(
        <section className='posts-list'>
            <h2>Lijst met Artikelen</h2>
            {renderedNews}
        
        </section>
    )

}
