import { useState, useRef } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { newsPost } from '../postsSlice'

import React from 'react';
import { useForm } from "react-hook-form";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup'


const SignupSchema = yup.object().shape({
	firstName: yup.string().required(),
	lastName: yup.string(),
	age: yup.number().required().positive().integer(),
	website: yup.string().url()
})
export const AddPostForm = () => {

  const inputRef = useRef()
  const [imageUrls, setImageUrls] = useState('')
  const [display, setDisplay] = useState('')
  const [title, setTitle] = useState('')
  const [discription, setDiscription] = useState('')
  const [subtitle, setSubtitle] = useState('')
  const [content, setContent] = useState('')



  const dispath = useDispatch();

  //users ophalen uit de Redux-store
  const provinces = useSelector(state => state.provinces)

  // const onPictureChanged = e => setImageUrls(e.target.value)
  const onDisplayChanged = e => setDisplay(e.target.value)
  const onTitleChanged = e => setTitle(e.target.value)
  const onDiscriptionChanged = e => setDiscription(e.target.value)
  const onSubtitleChanged = e => setSubtitle(e.target.value)
  const onContentChanged = e => setContent(e.target.value)

  const onSavePostCliked = () => {
    //je kunt script testen controleerd
    if (title && content) {
      //nieuws object gestuurd
      dispath(newsPost(imageUrls, display, title, discription, subtitle, content))
      setImageUrls('');
      setDisplay('');
      setTitle('');
      setDiscription('');
      setSubtitle('');
      setContent('');
    }

  }

  const provinceOptions = provinces.map(province => (
    <option key={province.id} value={province.city}>{province.city}</option>
  ));
 
  const {
		register,
		handleSubmit,
		formState: { errors }
	} = useForm({
		resolver: yupResolver(SignupSchema)
	})
	const [isShow, setIsShow] = React.useState(false)

	const onSubmit = (data) => {
		console.info(data)
	}

	const onError = (errors) => {
		console.error(errors)
	}


  
  return (
    <section>
      <h2>Add a News</h2>
      <form onSubmit={handleSubmit(onSubmit, onError)}>
      <label>First Name</label>
						<input type="text" {...register('firstName')} />
						{errors.firstName && <p>{errors.firstName.message}</p>}
        <label htmlFor="imageUrls">Picture :&nbsp;</label>
        <input
          type="file"
          onChange={() => setImageUrls(inputRef.current.files[0])}
          ref={inputRef}
          id="gallery-image"
          accept=".jpg, .jpeg, .png"
        />  
        <br/>
        {imageUrls?<img src={`assets/images/${imageUrls.name}`} className="img-fluid rounded-start" width="400"  alt="news_picture" />:null}

        <br/>
        <input
          type="hidden"
          id="imageUrls"
          name="imageUrls"
          value={imageUrls.name}
        />

        <label htmlFor="newsDisplay">Display:</label>
        <select id="display" value={display} onChange={onDisplayChanged}>
          <option value={""}></option>
          {provinceOptions}
        </select>

        <label htmlFor="newsTitle">Title :</label>
        <input
          type="text"
          id="newsTitle"
          name="newsTitle"
          value={title}
          onChange={onTitleChanged}
          // ref={register} 
          // className={`form-control ${errors.firstName ? 'is-invalid' : ''}`}
          
        />
       

        <label htmlFor="newsDiscription">Discription :</label>
        <textarea
          id="newsDiscription"
          name="newsDiscription"
          value={discription}
          onChange={onDiscriptionChanged}
        />

        <label htmlFor="newsSubtitle">Subtitel :</label>
        <input
          type="text"
          id="newsSubtitle"
          name="newsSubtitle"
          value={subtitle}
          onChange={onSubtitleChanged}
        />

        <label htmlFor="newsContent">Content :</label>
        <textarea
          id="newsContent"
          name="newsContent"
          value={content}
          onChange={onContentChanged}
        />
        <br />

        <button type="button" onClick={onSavePostCliked}>Save news</button>
        <br />
      </form>
    </section>
  )

}
