import {createAsyncThunk, createSlice, nanoid} from "@reduxjs/toolkit"
import { useDispatch } from "react-redux";





const initialState = {
    
  newsData : getNews(),
// newsData:[],
    status:'idle',
    error: null

  
}




//posts object


const postsSlice = createSlice({
    name:'news',
    initialState: getNews(),
    reducers:{



        postAdded: {
            reducer(state, action){
            state.newsData.push(action.payload)
            },
            prepare(title, content,userId){
                return{
                    payload:{
                        id:nanoid(),
                        title,
                        content,
                        date:new Date().toISOString(),
                        user:userId
                    }
                }
            }
        },

        newsAdded(state, action){
            // state.newsData.push(action.payload)
            // state:huidige state
            console.log('newsAdded =',action)
            // state.push(action.payload)
        },

        postUpdated(state, action){
            // in action.payload.id, .title, .content
            const {id, title, content}= action.payload
            const existingPost = state.news.find(post => post.id===id)

            if(existingPost){
                existingPost.title=title;
                existingPost.content=content;
            }

        }
    }
})

export const {postAdded,postUpdated,newsAdded} = postsSlice.actions
export default postsSlice.reducer

export const selectAllNews= state =>state.news.newsData
export const selectPostById = (state, postId) =>state.news.newsData.find(post => post.id === postId)
