import { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { postAdded } from '../postsSlice'

export const AddPostForm = () => {
  const [picture, setPicture] = useState('')
  const [displayId, setdisplayId] = useState('')
  const [title, setTitle] = useState('')
  const [content, setContent] = useState('')
  const [discription, setDiscription] = useState('')

  const dispath = useDispatch();

  //users ophalen uit de Redux-store
  const users = useSelector (state =>state.users)

  const onTitleChanged = e => setTitle(e.target.value)
  const onContentChanged = e => setContent(e.target.value)
  const onAuthorChanged = e => setUserId(e.target.value)

  const onSavePostCliked=() =>{
    //je kunt script testen controleerd
    if( title && content){
       //nieuws object gestuurd
      dispath(postAdded(title, content, userId))
      setTitle('');
      setContent('');
    }
   
  }

const userOptions =users.map(user=>(
  <option key={user.id} value={user.id}>{user.name}</option>
));
//title content userId
const canSave=Boolean(title) && Boolean(content) && Boolean(userId)

  return (
    <section>
      <h2>Add a News</h2>
      <form>
        <label htmlFor="newsPicture">Picture :</label>
        <input
          type="text"
          id="newsPicture"
          name="newsPicture"
          value={picture}
          onChange={onTitleChanged}
        />

        <label htmlFor="newsDisplay">Display:</label>
        <select id="display" value={displayId} onChange={onAuthorChanged}>
          <option value={""}></option>
          {userOptions}
        </select>

        <label htmlFor="postTitle">Title :</label>
        <input
          type="text"
          id="newsTitle"
          name="newsTitle"
          value={title}
          onChange={onTitleChanged}
        />

        <label htmlFor="postDiscription">Discription :</label>
        <textarea
          id="newsDiscription"
          name="newsDiscription"
          value={discription}
          onChange={onContentChanged}
        />

         <label htmlFor="newsSubtitle">Subtitel :</label>
        <input
          type="text"
          id="newsSubtitle"
          name="newsSubtitle"
          value={subtitle}
          onChange={onTitleChanged}
        />

        <label htmlFor="newsContent">Content :</label>
        <textarea
          id="newsContent"
          name="newsContent"
          value={content}
          onChange={onContentChanged}
        />
        <br/>

        <button type="button" disabled={!canSave} onClick={onSavePostCliked}>Save Post</button>
        <br/>
      </form>
    </section>
  )

