            initialState.newsData.push(data[0])
            console.log('url data: ',data,initialState.newsData)
            dispath(postAdded(title, content, userId))
            return data
            
           
        } catch (err) {
            // Handle Error Here
            console.error(err);
        }
    }
);

//posts object


const postsSlice = createSlice({
