import {createAsyncThunk, createSlice, nanoid} from "@reduxjs/toolkit"


const initialState = {
  newsData:[
    {id:'1', title:'Title1', content:'dit is wat tekst 1',date:'2016-01-04 10:34:23', user:'1'},
    {id:'2', title:'Title2', content:'dit is wat tekst 2',date:'2016-01-04 10:34:23',user:'2'}
    ],
// newsData:[],
    status:'idle',
    error: null
}

//api geven

export const getUserList = (page = 1) => async dispatch => {
    dispatch(getUserListStarted());
    try {
      const res = await fetch(`https://reqres.in/api/users?page=${page}`);
      const data = await res.json();
      dispatch(getUserListSuccess(data));
    } catch (err) {
      dispatch(getUserListFailure(err.message));
    }
  }
