import React from 'react'
import { useSelector,useDispatch } from 'react-redux'
 import { Link } from 'react-router-dom'
// import { PostAuthor } from './postAuthor'
import { selectNewsById } from '../features/postsSlice'

export const SingleNewsPage = ({ match }) => {
  //   //posts/:id
  const { Id } = match.params
  const dispatch = useDispatch()
  const news = useSelector(state =>selectNewsById(state, Id))

  if (!news) {
    return (
      <section>
        <h2>Post not found!</h2>
      </section>
    )
  }
  const onDeleteClicked = () => {
    
      dispatch(newsDelete({Id }))
     
   
  }

    return(
      <section>
      <article className="post mt-3">
        <img src={news.imageUrls} className="img-fluid rounded-start" alt="..."/>          
       
        <h2>{news.title}</h2>
        <p>{news.description}</p>

        <h3>{news.subtitle}</h3>
        <p>{news.content}</p>
      
        {/* <PostAuthor userId={post.user}/> */}
        <Link to={`/editNews/${Id}`} className="button">Bewerken</Link>
        <button type="button" onClick={onDeleteClicked}>
       delete
      </button>
      </article>
    </section>
    )
}
