import React from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
// import { PostAuthor } from './postAuthor'
import { selectNewsById } from '../features/postsSlice'

export const SingleNewsPage = ({ match }) => {
  //   //posts/:id
  const { Id } = match.params

  const news = useSelector(state =>selectNewsById(state, Id))

  if (!news) {
    return (
      <section>
        <h2>Post not found!</h2>
      </section>
    )
  }





 const renderedNews= news.map (news=>(      
        <article key={news.id} className='post-excerpt'>
           <div class="card mb-3">
                <div class="row g-0">
                    <div class="col-md-4">
                    <img src={news.imageUrls} class="img-fluid rounded-start" alt="..."/>
                    </div>
                    <div class="col-md-8">
                    <div class="card-body">
                        <p>news : {news.id}</p>
                        <strong>{news.displayTag}</strong>
                        <h5 class="card-title">{news.title}</h5>
                        <p class="card-text">{news.subtitle}</p>
                        
                    </div>
                    </div>
                </div>
            </div>
            <Link to={`/republiek/${news.id}`} className="button muted-button">View post</Link>
        </article>
    ));




    return(
        <section className='posts-list'>
            <h2>News</h2>
            {renderedNews}
        
        </section>
    )
}
