import { createSlice, nanoid} from "@reduxjs/toolkit"


const initialState = {

 newsData:[],
    status:'idle',
    error: null
}

//api geven

export const getNews = () => async dispatch => {
    try {
      const res = await fetch(`https://www.vrt.be/vrtnws/nl/regio/_jcr_content/par/grid.app.json`);
      const data = await res.json();
      dispatch(newsAdded(data));
    } catch (err) {
    //   dispatch(getUserListFailure(err.message));
    }
  }

const postsSlice = createSlice({
    name:'news',
    initialState,
    reducers:{


        postAdded: {
            reducer(state, action){
            state.newsData.push(action.payload)
            },
            prepare(title, content,userId){
                return{
                    payload:{
                        id:nanoid(),
                        title,
                        content,
                        date:new Date().toISOString(),
                        user:userId
                    }
                }
            }
        },

        newsAdded(state, action){
             state.newsData = action.payload
        },

        postUpdated(state, action){
            const {id, title, content}= action.payload
            const existingPost = state.news.find(post => post.id===id)

            if(existingPost){
                existingPost.title=title;
                existingPost.content=content;
            }

        }
    }
})

export const {postAdded,postUpdated,newsAdded} = postsSlice.actions
export default postsSlice.reducer

export const selectAllNews= state =>state.news.newsData
export const selectPostById = (state, postId) =>state.news.newsData.find(news => news === postId)
