import {useDispatch, useSelector} from "react-redux";
import { getPosts, selectAllPosts } from "../postsSlice";
import {useEffect} from "react";

  export const RepubliekList =()=>{

    const dispatch= useDispatch()

    //state.posts kom van postsSlice lijn10
    const news = useSelector(selectAllPosts);

    const newsStatus = useSelector (state=>state.posts.status)

    useEffect(()=>{
        if(newsStatus ==='idle'){
            dispatch(getPosts())

        };
    }, [newsStatus, dispatch])

//Hier zou je array kunnen sorteren

    // const renderedPosts= posts.map(post=>(
    //     <article key={post.id} className='post-excerpt'>
    //         <h3>{post.title}</h3>
    //         <p>{post.content}</p>
    //         <Link to={`/posts/${post.id}`} className="button muted-button">View post</Link>
    //     </article>
    // ));

      const republiekList= news.map(post=>(
        <article key={post.id} className='post-excerpt'>
            <div className="card mb-3" >
                    <div className="row g-0">
                        <div className="col-md-4">
                        {/* <img src="..." className="img-fluid rounded-start" alt="..."> */}
                        </div>
                        <div className="col-md-8">
                        <div className="card-body">
                            <h5 className="card-title">Card title</h5>
                            <p className="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                            <p className="card-text"><small className="text-muted">Last updated 3 mins ago</small></p>
                        </div>
                        </div>
                    </div>
                </div>
        </article>
    ));

    return(
        <section className='posts-list'>
            <h2>Lijst met Artikelen</h2>
            {republiekList}
        
        </section>
    )
        
    
}
